import React, { useEffect } from 'react';
import { Box, ModalUnstyled, Rating, Stack } from '@mui/material';
import { useParams } from 'react-router-dom';
import { connect, useDispatch, useSelector } from 'react-redux';
import { State } from '../redux/reducers/root-reducer';
import { Details, Genre } from '../types';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import HistoryIcon from '@mui/icons-material/History';
import StarIcon from '@mui/icons-material/Star';
import getMovie from '../redux/actions/movie.actions';
import BackButton from '../components/BackButton';
import Actor from '../components/Actor';
import '../styles/MoviePage.scss';
import Spinner from '../components/Spinner';

const MoviePage = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const movieId = String(id);

  useEffect(() => {
    dispatch(getMovie(movieId));
  }, [dispatch, movieId]);

  const base_url_for_backdrop = 'https://image.tmdb.org/t/p/w500';
  const base_url_for_poster = 'https://image.tmdb.org/t/p/w185';

  const { movie, fetched }: { movie: Details; fetched: boolean } = useSelector(
    (state: State) => state.movie
  );

  const {
    title,
    backdrop_path,
    poster_path,
    credits,
    tagline,
    overview,
    genres,
    release_date,
    runtime,
  } = movie;

  return (
    <>
      {fetched ? (
        <ModalUnstyled open={true} className='movie-page'>
          <div style={{ maxHeight: 'fit-content' }}>
            {fetched && (
              <Box
                className='container'
                sx={{
                  backgroundImage: `url(${
                    backdrop_path !== null
                      ? base_url_for_backdrop + backdrop_path
                      : poster_path !== null
                      ? base_url_for_poster + poster_path
                      : 'https://harriesopticians.com/wp-content/uploads/sites/30/2018/07/placeholder-coming-soon.jpg'
                  })`,
                  backgroundRepeat: 'no-repeat',
                  backgroundSize: 'cover',
                  backgroundPositionY: '40%',
                }}
              >
                <Box
                  className='inside-box'
                  sx={{
                    position: 'fixed',
                    top: 0,
                    width: '100%',
                    height: '100%',
                    backgroundColor: 'rgba(0, 0, 0, 0.3)',
                  }}
                >
                  <div className='group'>
                    <div
                      className='title_and_rating'
                      style={{
                        display: 'flex',
                        flexDirection: 'row',
                        alignItems: 'center',
                      }}
                    >
                      <h2>{movie.title} </h2>
                      <Stack spacing={1}>
                        <Rating
                          value={movie.vote_average * 0.5}
                          precision={0.1}
                          max={5}
                          emptyIcon={
                            <StarIcon
                              style={{ opacity: 1, color: '#fff' }}
                              fontSize='inherit'
                            />
                          }
                          readOnly
                        />
                      </Stack>
                    </div>
                    <div className='img-and-inf'>
                      <div className='image'>
                        <img
                          src={
                            poster_path !== null
                              ? base_url_for_poster + poster_path
                              : `data:image/png;base64,`
                          }
                          alt={title}
                        />
                      </div>

                      <div className='info'>
                        <h3 className='tagline'>{tagline}</h3>
                        <div className='about'>{overview}</div>
                        <div className='cast'>
                          {/* {credits.cast && <strong>Cast: </strong>} */}
                          {credits.cast.map((actor, index) => {
                            while (index < 11)
                              return <Actor key={index} actor={actor} />;
                          })}
                        </div>
                      </div>
                    </div>
                    <div className='details'>
                      <div className='genres'>
                        {genres && <strong className='strong'>Genre: </strong>}
                        {genres.map((gen: Genre) => {
                          return (
                            <span className='genre' key={gen.id}>
                              {gen.name}
                            </span>
                          );
                        })}
                      </div>
                      <div className='other-info'>
                        {release_date && (
                          <div className='duration'>
                            <AccessTimeIcon />
                            <strong className='strong'>Released: </strong>
                            <div className='release_and_duration'>
                              {` ${release_date}`}
                            </div>
                          </div>
                        )}
                        {runtime !== 0 && (
                          <div className='duration'>
                            <HistoryIcon />
                            <strong className='strong'>Duration: </strong>
                            <div className='release_and_duration'>
                              {runtime} minutes
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                  <BackButton />
                </Box>
              </Box>
            )}
          </div>
        </ModalUnstyled>
      ) : (
        <Spinner loading={fetched} />
      )}
    </>
  );
};

const mapStateToProps = (state: State) => ({
  movie: state.movie.movie,
  fetched: state.movie.fetched,
});

export default connect(mapStateToProps)(MoviePage);
